  def check_code(self, code=""):
    """
    Checks if a given code is valid and processes it.

    Args:
        code: The code string to check.

    Returns:
        An integer status code (200 for win, game number, or 404 for invalid).
    """
    if code in self.codes:
        if code[5:8] == 'win':
            self.codes.remove(code)
            return 200
        self.codes.remove(code)
        return int(code[4])
    else:
        return 404

  async def is_admin(self, username):
    """
    Checks if a given username is registered as a super admin.

    Args:
        username: The username to check.

    Returns:
        The admin user dictionary if found, otherwise False.
    """
    for user in self.admins:
        if user["username"] == username:
            return user
    
    return False

  def reset(self):
    """
    Resets the game by restoring the codes list to its initial state.
    """
    self.codes = list(self.backcodes)


